settings.custom_css:
button {
  border: none;
  -webkit-appearance: none;
          appearance: none;
 border-bottom: 1px solid #F5F5F5;
}
button {
  background: transparent;
  position: relative;
  padding: 24px 20px 24px 40px;
 border-bottom: 1px solid #F5F5F5;
  text-align: left;
  display: block;
  cursor: pointer;
  width: 100%;
  outline: none;
  text-transform: initial;
  min-height: auto;
  height: auto;
  line-height: inherit;
  font-weight: bold;
  box-shadow: none;
  border-radius: 0px;
  font-size: 18px;
}
button:hover,
.faqAccordion > dt > button:focus {
  background: white;
  border: none;
 border-bottom: 1px solid #F5F5F5;
  color: #C7A55D;
 }
